package forloop;
 import java.util.Scanner;
public class exp2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Enter the numbers you want:-");
		Scanner sc=new Scanner(System.in);
		
		int num=sc.nextInt();
		
		for (int i=1;i<=num;i++) {
			System.out.println(i);
		}

	}

}
